CREATE PROCEDURE prcInitAssemply
AS
BEGIN
	DECLARE @sql nVARCHAR(256)
	DECLARE @LoginName nvarchar(256) = (SELECT TOP 1 name FROM sys.server_principals WHERE type_desc = 'SQL_LOGIN' AND is_disabled = 'false' ORDER BY principal_id ASC );
	DECLARE @masterOwnerSID varbinary(85);
	DECLARE @localOwnerSID varbinary(85);
	
	SELECT  @masterOwnerSID = SD.[SID]
	FROM  master..sysdatabases SD 
	WHERE  SD.Name = DB_NAME()

	SELECT @localOwnerSID = [SID]
	From sys.database_principals
	WHERE Name = 'DBO'

	IF(@masterOwnerSID <> @localOwnerSID)
	BEGIN
		SET @sql  = 'ALTER AUTHORIZATION ON DATABASE::' + quotename(db_name()) + ' TO ' + quotename(@LoginName); 
		EXEC(@sql)
	END
	----------------------------------------------------------------------------------------------------------
	IF(SELECT is_trustworthy_on FROM sys.databases WHERE name = db_name()) = 0
	BEGIN
		SET @sql='ALTER DATABASE '+ quotename(db_name()) + ' SET TRUSTWORTHY ON;'
		EXEC(@sql)
	END
	----------------------------------------------------------------------------------------------------------
	IF(SELECT value_in_use FROM sys.configurations WHERE name = 'clr enabled') = 0
	BEGIN
		EXEC sp_configure 'show advanced options', 1;
		RECONFIGURE;
		EXEC sp_configure 'clr enabled', 1;
		RECONFIGURE;
	END
END
