CREATE PROCEDURE NSSendMail(
	@message XML,
	@attachmentFile VARBINARY(MAX),
	@attachmentName NVARCHAR(256),
	@messageSubject NVARCHAR(256) OUTPUT,
	@messageBody NVARCHAR(max) OUTPUT,
	@mailAddress NVARCHAR(100) OUTPUT,
	@errorMessage NVARCHAR(max) OUTPUT)
AS
BEGIN
	IF NOT EXISTS (SELECT * FROM SYS.objects WHERE NAME LIKE 'NSCLRSendMail')
	BEGIN
		SET @messageSubject = N'' SET @messageBody = N'' SET @mailAddress = N'' SET @errorMessage = N'Procedure NSCLRSendMail does not exist'
		RETURN 0
	END
	DECLARE @sendresult		INT
	EXEC @sendresult = NSCLRSendMail @message, @attachmentFile, @attachmentName, @messageSubject output, @messageBody output, @mailAddress output, @errorMessage output
	RETURN @sendresult
END
